let favorites = [];
let currentCategory = '';
let currentView = 'home';
let pendingSearchQuery = null;

function getAllPrayers() {
return prayers.morning.concat(prayers.evening, prayers.special);
}

function renderPrayerList(prayersToRender, emptyStateMessage = 'No prayers available right now.') {
const prayerList = document.getElementById('prayerList');
prayerList.innerHTML = '';

if (prayersToRender.length === 0) {
prayerList.innerHTML = `<p style="text-align: center; color: var(--text-secondary);">${emptyStateMessage}</p>`;
return;
}

prayersToRender.forEach(prayer => {
const isFav = favorites.includes(prayer.id);
const prayerItem = document.createElement('div');
prayerItem.className = 'prayer-item';
prayerItem.innerHTML = `
<h3>
${prayer.title}
<span class="favorite-icon ${isFav ? 'active' : ''}" onclick="toggleFavorite(${prayer.id})">
${isFav ? '‚ù§Ô∏è' : 'ü§ç'}
</span>
</h3>
<p class="prayer-text">${prayer.text}</p>
`;
prayerList.appendChild(prayerItem);
});
}

function updateNavLinks(activeSection) {
document.querySelectorAll('.nav-link').forEach(link => {
const target = link.getAttribute('href').replace('#', '') || 'home';
if (target === activeSection) link.classList.add('active');
else link.classList.remove('active');
});
}

function showHomeSection() {
document.querySelector('.home-page').classList.add('active');
document.querySelector('.prayers-page').classList.remove('active');
document.querySelector('.prayer-display-page').classList.remove('active');
backToCategories();
currentCategory = '';
currentView = 'home';
updateNavLinks('home');
}

function showCategoriesSection() {
document.querySelector('.home-page').classList.remove('active');
document.querySelector('.prayers-page').classList.add('active');
document.querySelector('.prayer-display-page').classList.remove('active');
backToCategories();
currentView = 'categories';
updateNavLinks('categories');
}

function toggleDarkMode() {
document.body.classList.toggle('dark-mode');
const isDark = document.body.classList.contains('dark-mode');
document.querySelector('.dark-mode-toggle').textContent = isDark ? '‚òÄÔ∏è' : 'üåô';
}

function enterPrayerBook() {
if (window.location.hash !== '#categories') {
window.location.hash = '#categories';
} else {
showCategoriesSection();
}
}

function goHome() {
if (window.location.hash !== '#home') {
window.location.hash = '#home';
} else {
showHomeSection();
}
}

function showCategory(category) {
currentCategory = category;
if (category === 'morning') {
showPrayerDisplayPage(category);
} else {
document.getElementById('categoriesView').style.display = 'none';
document.getElementById('prayerListView').style.display = 'block';
const titles = {
evening: 'üåô Evening Prayers',
special: '‚ú® Special Prayers',
favorites: '‚ù§Ô∏è My Favorites'
};
document.getElementById('categoryTitle').textContent = titles[category];
const prayersToShow = category === 'favorites'
? getAllPrayers().filter(p => favorites.includes(p.id))
: prayers[category];
const emptyStateMessage = category === 'favorites'
? 'No favorite prayers yet. Click the heart icon on any prayer to add it to your favorites!'
: 'No prayers available in this category right now.';
renderPrayerList(prayersToShow, emptyStateMessage);
}
}

function showPrayerDisplayPage(category) {
document.querySelector('.prayers-page').classList.remove('active');
document.querySelector('.prayer-display-page').classList.add('active');

const container = document.getElementById('prayerCardsContainer');
container.innerHTML = '';

prayers[category].forEach(prayer => {
const prayerCard = document.createElement('div');
prayerCard.className = 'prayer-card';
prayerCard.innerHTML = `
<div class="prayer-card-header" onclick="togglePrayerCard(this)">
${prayer.title}
</div>
<div class="prayer-card-content">
${prayer.image ? `<div class="prayer-image"><img src="/static/${prayer.image}" alt="${prayer.title} illustration"></div>` : ''}
<div class="prayer-text tibetan-text truncatable">${prayer.text}</div>
${prayer.translation ? `<div class="english-translation truncatable">${prayer.translation}</div>` : ''}
<div class="read-more-container">
<button class="read-more-btn" onclick="toggleReadMore(this)">Show More</button>
</div>
</div>
`;
container.appendChild(prayerCard);
});
}

function togglePrayerCard(header) {
const content = header.nextElementSibling;
const isExpanded = content.classList.contains('expanded');

if (isExpanded) {
content.classList.remove('expanded');
header.classList.remove('expanded');
} else {
content.classList.add('expanded');
header.classList.add('expanded');
}
}

function backToPrayerList() {
document.querySelector('.prayer-display-page').classList.remove('active');
document.querySelector('.prayers-page').classList.add('active');
}

function backToCategories() {
document.getElementById('prayerListView').style.display = 'none';
document.getElementById('categoriesView').style.display = 'block';
document.querySelector('.prayer-display-page').classList.remove('active');
document.getElementById('categoryTitle').textContent = '';
document.getElementById('prayerList').innerHTML = '';
currentCategory = '';
}

function toggleFavorite(prayerId) {
const index = favorites.indexOf(prayerId);
if (index > -1) {
favorites.splice(index, 1);
} else {
favorites.push(prayerId);
}
if (currentCategory === 'morning') showCategory('morning');
else if (currentCategory === 'evening') showCategory('evening');
else if (currentCategory === 'special') showCategory('special');
else if (currentCategory === 'favorites') showCategory('favorites');
else if (currentCategory === 'search') {
const searchInput = document.getElementById('prayerSearchInput');
if (searchInput) handleSearch(searchInput.value);
}
}

function handleSearch(rawQuery, options) {
options = options || {};
const skipHashSync = !!options.skipHashSync;
const query = rawQuery.trim().toLowerCase();

if (!query) {
pendingSearchQuery = null;
if (window.location.hash === '#categories') showCategoriesSection();
else showHomeSection();
return;
}

if (!skipHashSync && window.location.hash !== '#categories') {
pendingSearchQuery = query;
window.location.hash = '#categories';
return;
}

showCategoriesSection();
document.getElementById('categoriesView').style.display = 'none';
document.getElementById('prayerListView').style.display = 'block';
document.getElementById('categoryTitle').textContent = 'üîç Search Results';
const searchResults = getAllPrayers().filter(prayer => {
const haystack = `${prayer.title} ${prayer.text} ${prayer.translation || ''}`.toLowerCase();
return haystack.includes(query);
});
renderPrayerList(searchResults, 'No prayers match your search. Try a different phrase.');
currentCategory = 'search';
}

function clearSearch() {
const searchInput = document.getElementById('prayerSearchInput');
if (searchInput) {
searchInput.value = '';
}
handleSearch('');
}

function toggleReadMore(button) {
const content = button.closest('.prayer-card-content');
const isExpanded = content.classList.toggle('show-full-text');
button.textContent = isExpanded ? 'Show Less' : 'Show More';
}

function handleHashNavigation() {
const section = window.location.hash.replace('#', '') || 'home';
if (section === 'categories') showCategoriesSection();
else showHomeSection();

if (pendingSearchQuery) {
const queryToRun = pendingSearchQuery;
pendingSearchQuery = null;
handleSearch(queryToRun, { skipHashSync: true });
}
}

handleHashNavigation();
window.addEventListener('hashchange', handleHashNavigation);
